import React from 'react';
import classes from './RightMenu.css';
import Radium from 'radium';
import { Icon } from 'react-icons-kit';
import {plus} from 'react-icons-kit/feather/plus';
import {minus} from 'react-icons-kit/feather/minus';
import {search} from 'react-icons-kit/feather/search';
import {menu} from 'react-icons-kit/feather/menu'

const rightMenu = (props) => {
     let modal = null;
      if (props.modal === true){
        modal = props.close;
      } else {
        modal = props.open;
      }
    return (        
      <div className={classes.RightMenu}>
          <div className={classes.width + ' ' + classes.left}>
            <Icon className={classes.iconLeft} size={40} icon={minus}></Icon>
            <Icon className={classes.iconLeft} size={40} icon={plus}></Icon>
          </div>
          <div className={classes.width + ' ' + classes.mid}>
            <Icon onClick={modal} className={classes.iconLeft} size={35} icon={search}></Icon>
            <Icon onClick={() => {props.menu(); document.getElementById('pastille').style.visibility = 'hidden';}} className={classes.iconLeft} size={40} icon={menu}></Icon>
          </div>
      </div>
    );
}

export default Radium(rightMenu);
