import React from 'react';
import classes from './underCategories.css';
import Radium from 'radium';

let style = {
    firstContainer: {
    },
    secondContainer: {
    }
}

let title = null;

const underCategories = (props) => {
    style = {
        firstContainer: {
            backgroundColor: 'rgb(55, 86, 157)',
            margin: '0px 2px',
            height: '550px',
            width: '25%',
            flexDirection: 'column'
        },
        secondContainer: {
            backgroundColor: 'rgb(55, 86, 157)',
            margin: '0px 2px',
            height: '550px',
            width: '75%',
            flexDirection: 'column'
        }
    }

    title = {
        black: 'Objets/IoT',
        grey: 'Accueil / '
    }

    return (
        <div className={classes.themes} id="underCat">
          <div className={classes.modalMiddle}>
            <div className={classes.sectionTitle}>
                <p style={{color: 'black', fontWeight: 'bold'}}><span onClick={() => {props.nameSpectre('Vide'); props.closeCategorie(); props.openSpectre();}} style={{cursor: 'pointer', color: 'rgb(147, 147, 147)'}}>{title.grey}</span>{title.black}</p>
            </div>
            <div className={classes.fullWidthContainer}>
                <div style={style.firstContainer}>
                    <div className={classes.decision}>
                        <button className={classes.button + ' ' + classes.buttonObjet}><a href="http://www.arcep.fr/uploads/tx_gsavis/15-0830.pdf">Décision</a></button>
                    </div>
                    <div className={classes.alignContainer}>
                    </div>
                </div>
                <div style={style.secondContainer}>
                    <div className={classes.decision}>
                        <button className={classes.button + ' ' + classes.buttonObjet}><a href="https://www.arcep.fr/uploads/tx_gsavis/16-1130.pdf">Décision</a></button>
                    </div>
                    <div className={classes.alignContainer}>
                    </div>
                </div>
            </div>
        </div>
      </div>
    )
}
export default Radium(underCategories);
