import React, { Component } from 'react';
import Header from './components/Header/Header';
import Radium, { StyleRoot } from 'radium';
import classes from './App.css';
import Container from './components/Container/Container';
import Footer from './components/Footer/Footer';
import Modal from 'react-modal';
import bg from './assets/bgmodal.png'

const modalStyle = { 
  overlay: {
    width: '100vw',
    flexGrow: '1',
    zIndex: '10',
    marginBottom: '80px',
  }, 
  content: {
    backgroundColor: 'yellow',
    width: '100%',
    height: '100%',
    top: '0',
    bottom: '0',
    left: '0',
    right: '0',
    padding: '0',
    justifyContent: 'flex-end',
    border: '0px',
    borderRadius: '0px'
  }
}
class App extends Component {
  state = {
    modalIsOpen: false,
    rightModalIsOpen: false,
  }

  openModal () {
    this.setState({modalIsOpen: true});
  }

  closeModal () {
    this.setState({modalIsOpen: false});
  }

  openRightModal () {
    this.setState({rightModalIsOpen: true});
  }

  closeRightModal () {
    this.setState({rightModalIsOpen: false});
  }

  render() {
    this.openRightModal = this.openRightModal.bind(this);
    this.closeRightModal = this.closeRightModal.bind(this);
    return (
      <StyleRoot>
        <div className={classes.App}>
          <Modal 
              isOpen={this.state.modalIsOpen}
              onRequestClose={() => {this.setState({modalIsOpen: false})}}
              ariaHideApp={false}
              style={modalStyle}
              >
          </Modal>
          <Header open={this.openRightModal} close={this.closeRightModal} modal={this.state.rightModalIsOpen}/>
          <Container open={this.openRightModal} close={this.closeRightModal} modal={this.state.rightModalIsOpen}/>
          <Footer />
        </div>
      </StyleRoot>
    );
  }
}

export default Radium(App);
