import React, { Component } from 'react';
import classes from './Header.css';
import LogoSpectre from './Logo/logo';
import Radium from 'radium';
import Modal from 'react-modal';
import SpectreTopBar from './SpectreTopBar/SpectreTopBar';
import RightMenu from './RightMenu/RightMenu';
import ModalTopBar from './ModalTopBar/ModalTopBar';

const modalStyle = { 
    overlay: {
      width: '100vw',
      height: '100vh',
      zIndex: '1',
    }, 
    content: {
      backgroundColor: 'rgb(221, 96, 92)',
      width: '100%',
      height: '100%',
      top: '0',
      bottom: '0',
      left: '0',
      right: '0',
      padding: '0',
      justifyContent: 'flex-end',
      border: '0px',
      borderRadius: '0px'
    }
}

class header extends Component {
  state = {
    modalIsOpen: false,
    leftModalIsOpen: false,
    rightModalIsOpen: false,
  }

  openModal () {
    this.setState({modalIsOpen: true})
  }

  closeModal () {
    this.setState({modalIsOpen: true});
    document.getElementById('pastille').style.display = 'flex';
  }

  openLeftModal () {
    this.setState({leftModalIsOpen: true})
  }

  openRightModal () {
    this.setState({rightModalIsOpen: true})
  }

  render() {
    this.openModal = this.openModal.bind(this);
    this.closeModal = this.closeModal.bind(this);
    this.openLeftModal = this.openLeftModal.bind(this);
    this.openRightModal = this.openRightModal.bind(this);

    if (this.state.modalIsOpen)
      document.getElementById('pastille').style.display = 'none'

    return (        
      <div className={classes.Header}>
        <LogoSpectre/>
        <SpectreTopBar/>
        <RightMenu menu={this.openModal}/>
        <Modal 
          isOpen={this.state.modalIsOpen}
          onRequestClose={() => {this.setState({modalIsOpen: false})}}
          ariaHideApp={false}
          style={modalStyle}>
          <ModalTopBar />
        </Modal>
      </div>
    );
  }
}

export default Radium(header);
