import React from 'react';
import classes from './LeftModal.css';
import Radium from 'radium';
import { Icon } from 'react-icons-kit';
import {x} from 'react-icons-kit/feather/x';
import {home} from 'react-icons-kit/feather/home';
import {androidBus} from 'react-icons-kit/ionicons/androidBus';
import {calendar} from 'react-icons-kit/feather/calendar';
import {radio} from 'react-icons-kit/feather/radio';



const leftMdoal = (props) => {
    return (        
      <div className={classes.leftModal}>
          <div className={classes.Cross}>
            <Icon onClick={props.leftClose} icon={x} size={30} className={classes.crossStyle}/> 
          </div>
          <div className={classes.content}>
            <div className={classes.title}>
                <p>Découvrir nos thémathiques</p>
            </div>
            <div className={classes.allTheme}>
                <div className={classes.theme} onClick={() => {props.theme(1)}}>
                    <Icon style={{alignItems: 'center', display: 'flex'}} icon={home} size={30}/>
                    <p style={{margin: '20px 0px 20px 10px'}}>À la maison</p>
                </div>
                <div className={classes.theme}>
                    <Icon onClick={() => {props.theme(2)}} style={{alignItems: 'center', display: 'flex'}} icon={androidBus} size={30}/>
                    <p onClick={() => {props.theme(2)}} style={{margin: '20px 0px 20px 10px'}}>Transports en commun</p>
                </div>
                <div className={classes.theme}>
                    <Icon style={{alignItems: 'center', display: 'flex'}} icon={calendar} size={30}/>
                    <p style={{margin: '20px 0px 20px 10px'}}>Concerts et évènements sportifs</p>
                </div>
                <div className={classes.theme}>
                    <Icon style={{alignItems: 'center', display: 'flex'}} icon={radio} size={30}/>
                    <p style={{margin: '20px 0px 20px 10px'}}>Internet des objets / Smart cities</p>
                </div>
            </div>
          </div>
          <div className={classes.button}>
                <button onClick={props.openSecond} className={classes.styleButton}>Plus de thèmes</button>
            </div>
      </div>
    );
}

export default Radium(leftMdoal);
