import React, { Component } from 'react';
import classes from './Container.css';
import Radium from 'radium';
import Pastille from './Pastille/Pastille';
import Modal from 'react-modal';
import LeftModal from './LeftModal/LeftModal';
import RightModal from './RightModal/RightModal';
import SecondLeftModal from './SecondLeftModal/SecondLeftModal';

const leftModalStyle = { 
  overlay: {
    width: '340px',
    flexGrow: '1',
    zIndex: '1',
    boxShadow: '1px 0px 1px rgba(0, 0, 0, 0.15)',
    marginTop: '80px',
  }, 
  content: {
    backgroundColor: 'wihte',
    width: '100%',
    height: '100%',
    top: '0',
    bottom: '0',
    left: '0',
    right: '0',
    padding: '0',
    justifyContent: 'flex-end',
    border: '0px',
    borderRadius: '0px'
  }
}

const leftSecondModalStyle = { 
  overlay: {
    width: '100%',
    flexGrow: '1',
    zIndex: '1',
    boxShadow: '1px 0px 1px rgba(0, 0, 0, 0.15)',
    marginTop: '80px',
    backgroundColor: 'rgba(0, 0, 0, 0.15)',
  }, 
  content: {
    backgroundColor: 'rgba(0, 0, 0, 0.45)',
    width: '100%',
    height: '100%',
    top: '0',
    bottom: '0',
    left: '0',
    right: '0',
    padding: '0',
    justifyContent: 'flex-end',
    border: '0px',
    borderRadius: '0px'
  }
}

const rightModalStyle = { 
  overlay: {
    width: '340px',
    flexGrow: '1',
    zIndex: '1',
    boxShadow: '1px 0px 1px rgba(0, 0, 0, 0.15)',
    marginTop: '80px',
    alignSelf: 'flex-end',
    left: 'auto'
  }, 
  content: {
    backgroundColor: 'wihte',
    width: '100%',
    height: '100%',
    top: '0',
    bottom: '0',
    left: 'auto',
    right: '0',
    padding: '0',
    justifyContent: 'flex-end',
    border: '0px',
    borderRadius: '0px'
  }
}

const middleModal = (
  <div className={classes.modalMiddle}>
    <div className={classes.sectionTitle}>
      <p><span style={{color: 'black'}}>À la maison,</span> qu’est-ce qui m’entoure dans mon appartement ?</p>
    </div>
    <div className={classes.fullWidthContainer}>
      <div className={classes.firstContainer}></div>
      <div className={classes.secondContainer}></div>
      <div className={classes.thirdContainer}></div>
      <div className={classes.fourthContainer}></div>
    </div>
  </div>
)

class container extends Component {
    state = {
        page: '',
    }

    render (){

        return (        
        <div className={classes.container}>
          {middleModal}
          <Modal 
            isOpen={this.props.modalThird}
            onRequestClose={this.props.closeThird}
            ariaHideApp={false}
            style={leftModalStyle}
            >
              <LeftModal 
                leftClose={this.props.closeThird}
                openSecond={this.props.openSecond}
                closeSecond={this.props.closeSecond}/>
            </Modal>
            <Pastille
              leftOpen={this.props.openThird}
              rightOpen={this.props.open}
              />
            <Modal 
              isOpen={this.props.modal}
              onRequestClose={this.props.close}
              ariaHideApp={false}
              style={rightModalStyle}
              >
              <RightModal 
                rightClose={this.props.close}/>
            </Modal>
            <Modal 
              isOpen={this.props.modalSecond}
              onRequestClose={this.props.closeSecond}
              ariaHideApp={false}
              style={leftSecondModalStyle}
              >
                <SecondLeftModal
                  close={this.props.closeSecond}/>
            </Modal>
        </div>
        );
    }
}

export default Radium(container);
