import React from 'react';
import classes from './SecondLeftModal.css';
import Radium from 'radium';
import { Icon } from 'react-icons-kit';
import {x} from 'react-icons-kit/feather/x';
import {home} from 'react-icons-kit/feather/home';
import {androidBus} from 'react-icons-kit/ionicons/androidBus';
import {calendar} from 'react-icons-kit/feather/calendar';
import {radio} from 'react-icons-kit/feather/radio';


//noir
import { ReactComponent as Transport} from '../../../assets/noir/arcep_picto_noir_transports.svg';
import { ReactComponent as Maison} from '../../../assets/noir/arcep_picto_noir_maison.svg';
import { ReactComponent as Concert} from '../../../assets/noir/arcep_picto_noir_events_concerts.svg';
import { ReactComponent as SmartCity} from '../../../assets/noir/arcep_picto_noir_smart_cities.svg';

//blanc
import { ReactComponent as ConcertBlanc} from '../../../assets/blanc/arcep_picto_events_concerts.svg';
import { ReactComponent as SmartCityBlanc} from '../../../assets/blanc/arcep_picto_smart_cities.svg';
import { ReactComponent as MaisonBlanche} from '../../../assets/blanc/arcep_picto_maison.svg';
import { ReactComponent as TransportBlanc} from '../../../assets/blanc/arcep_picto_transports.svg';

let pictoBlanc = {
  maison: (<Maison  className={classes.logoUnderCat}/>),
  transport: (<Transport className={classes.logoUnderCat} />),
  concert: (<Concert className={classes.logoUnderCat} />),
  smartcity: (<SmartCity className={classes.logoUnderCat} />)
}

const secondLeftModal = (props) => {
  // if (props.un === true){
  //   pictoBlanc.maison = (<MaisonBlanche  className={classes.logoUnderCat}/>)
  // } else if (props.deux === true){
  //   pictoBlanc.transport = (<TransportBlanc className={classes.logoUnderCat} />)
  // } else if (props.trois === true){
  //   pictoBlanc.concert = (<ConcertBlanc className={classes.logoUnderCat} />)
  // } else if (props.quatre === true){
  //   pictoBlanc.smartcity = (<SmartCity className={classes.logoUnderCat} />)
  // }
    return (        
      <div className={classes.SecondLeftModal}>
        <div className={classes.Button}>
          <Icon size={30} icon={x} className={classes.Cross} onClick={props.close}/>
        </div>
        <div className={classes.Title}>
          <p>Les ondes qui vous entourent ?<br/> Venez <span> découvrir nos thématiques</span>.</p>
        </div>
        <div className={classes.Theme}>
          <div className={classes.rowSquare}>
            <div className={classes.square} onMouseOver={props.openHoverUnHandler} onMouseOut={props.closeHoverUnHandler} onClick={() => {props.closeUnderCategorie(); props.theme(1); props.closeSpectre();  props.closeCategorie(); props.close();}}>
              {props.un ? <MaisonBlanche  className={classes.logoUnderCat}/> : <Maison  className={classes.logoUnderCat}/>}
              <p >À la maison</p>
            </div>
            <div className={classes.square} onMouseOver={props.openHoverDeuxHandler} onMouseOut={props.closeHoverDeuxHandler} onClick={() => {props.closeUnderCategorie(); props.theme(2); props.closeSpectre();  props.closeCategorie(); props.close();}}>
              {props.un ? <MaisonBlanche  className={classes.logoUnderCat}/> : <Maison  className={classes.logoUnderCat}/>}
              <p>Transports en commun</p>
            </div>
          </div>
          <div className={classes.rowSquare}>
            <div className={classes.square} onMouseOver={props.openHoverTroisHandler} onMouseOut={props.closeHoverTroisHandler} onClick={() => {props.closeUnderCategorie(); props.theme(3); props.closeSpectre();  props.closeCategorie(); props.close();}}>
            {props.un ? <MaisonBlanche  className={classes.logoUnderCat}/> : <Maison  className={classes.logoUnderCat}/>}
              <p>Concerts et évènements sportifs</p>
            </div>
            <div className={classes.square} onMouseOver={props.openHoverQuatreHandler} onMouseOut={props.closeHoverQuatreHandler} onClick={() => {props.closeUnderCategorie(); props.theme(4); props.closeSpectre();  props.closeCategorie(); props.close();}}>
            {props.un ? <MaisonBlanche  className={classes.logoUnderCat}/> : <Maison  className={classes.logoUnderCat}/>}
              <p>Internet des objets / Smart cities</p>
            </div>
          </div>
        </div>
      </div>
    );
}

export default Radium(secondLeftModal);
