import React, { Component } from 'react';
import classes from './Header.css';
import LogoSpectre from './Logo/logo';
import Radium from 'radium';
import Modal from 'react-modal';
import SpectreTopBar from './SpectreTopBar/SpectreTopBar';
import RightMenu from './RightMenu/RightMenu';
import { Icon } from 'react-icons-kit';
import {x} from 'react-icons-kit/feather/x'

const modalStyle = { 
    overlay: {
      width: '100vw',
      height: '100vh',
      zIndex: '1',
    }, 
    content: {
      backgroundColor: 'rgb(221, 96, 92)',
      width: '100%',
      height: '100%',
      top: '0',
      bottom: '0',
      left: '0',
      right: '0',
      padding: '0',
      justifyContent: 'flex-end',
      border: '0px',
      borderRadius: '0px'
    }
}

class header extends Component {
  state = {
    modalIsOpen: false,
  }

  openModal () {
    this.setState({modalIsOpen: true})
  }

  render() {
    this.openModal = this.openModal.bind(this);
    let contentModal = (
      <div className={classes.contentModal}>
        <div className={classes.closeCrosse}>
          <Icon onClick={() => {this.setState({modalIsOpen: false})}} icon={x} size={50} style={{color: 'white', cursor: 'pointer'}}/>
        </div>
        <div className={classes.modal}>
          <p className={classes.parag}>Accueil</p>
          <p className={classes.parag}>Présentation de l'outil</p>
          <p className={classes.parag}>Découvrir nos thématiques</p>
          <p className={classes.parag}><a href="https://www.arcep.fr">À propos de l'Arcep</a></p>
        </div>
      </div>
    )

    if (this.state.modalIsOpen)
      document.getElementById('pastille').style.display = 'none'

    return (        
      <div className={classes.Header}>
        <LogoSpectre/>
        <SpectreTopBar/>
        <RightMenu menu={this.openModal}/>
        <Modal 
          isOpen={this.state.modalIsOpen}
          onRequestClose={() => {this.setState({modalIsOpen: false}); document.getElementById('pastille').style.display = 'flex'}}
          ariaHideApp={false}
          style={modalStyle}>
          {contentModal}
        </Modal>
      </div>
    );
  }
}

export default Radium(header);
