import React, {Component} from 'react';
import * as d3 from "d3";
import datas from '../../../assets/baseddarcep.json';

class BarChart extends Component {
    test() {
        console.log('ok');
    }


    drawChart() {
        const xscale = d3.scaleLinear();
                xscale.domain([0, 275000000000]);
                xscale.range([0, this.props.width]);

        const xAxis = d3.axisBottom()
                        .scale(xscale)

        const svg = d3.select("#spectre")
                        .append("svg")
                        .attr("width", this.props.width)
                        .attr("height", this.props.height)
                    svg.selectAll("rect")
                        .data(datas)
                        .enter()
                        .append("rect")
                        .on('mouseover',(d, i) => {
                            if (d.Catégorie === 'Objets/IoT'){
                               d3.select(this).transition()
                                    .duration('50')
                                    .attr('opacity', '.85');
                            } else if (d.Catégorie === 'Réseaux mobile et THD'){
                               d3.select(this).transition()
                                    .duration('50')
                                    .attr('opacity', '.85');
                            }  else if (d.Catégorie === 'Amateur'){
                               d3.select(this).transition()
                                    .duration('50')
                                    .attr('opacity', '.85');
