                               d3.select(this).transition()
                                    .duration('50')
                                    .attr('opacity', '.85');
                            } else if (d.Catégorie === 'Satellite'){
                               d3.select(this).transition()
                                    .duration('50')
                                    .attr('opacity', '.85');
                            } else if (d.Catégorie === 'Faisceaux hertziens'){
                               d3.select(this).transition()
                                    .duration('50')
                                    .attr('opacity', '.85');
                            } else if (d.Catégorie === 'Autres'){
                               d3.select(this).transition()
                                    .duration('50')
                                    .attr('opacity', '.85');
                            }
                        }) 
                        .on("click", (d, i) => {
                            if (d.Catégorie === 'Objets/IoT'){
                                this.props.categorie('Objets/IoT')
                            } else if (d.Catégorie === 'Réseaux mobile et THD'){
                                this.props.categorie('Réseaux mobile et THD')
                            }  else if (d.Catégorie === 'Amateur'){
                                this.props.categorie('Amateur')
                            } else if (d.Catégorie === 'PMSE'){
                                this.props.categorie('PMSE')
                            } else if (d.Catégorie === 'Satellite'){
                                this.props.categorie('Satellite')
                            } else if (d.Catégorie === 'Faisceaux hertziens'){
                                this.props.categorie('Faisceaux hertziens')
                            } else if (d.Catégorie === 'Autres'){
                                this.props.categorie('Autres')
                            }
                        })
                        .attr("x", (d, i) => {
                            if (xscale(d.Fréquencebasse) < 1) 
                                return 10 
                            else 
                                return xscale(d.Fréquencebasse)
                        })
                        .attr("y", (d, i) => {
                            if (d.Catégorie === 'Objets/IoT'){
                                return 0
                            } else if (d.Catégorie === 'Réseaux mobile et THD'){
                                return 150
                            }  else if (d.Catégorie === 'Amateur'){
                                return 20
                            } else if (d.Catégorie === 'PMSE'){
                                return 350
                            } else if (d.Catégorie === 'Satellite'){
                                return 180
                            } else if (d.Catégorie === 'Faisceaux hertziens'){
                                return 480
                            } else if (d.Catégorie === 'Autres'){
                                return 0
                            }
                        })
                        .attr("width", (d, i) => {
                                if (xscale(d.Fréquencehaute - d.Fréquencebasse) < 1)
                                    return 10
                                else 
                                    return xscale(d.Fréquencehaute - d.Fréquencebasse)
                        })
                        .attr("height", (d, i) => {
                            if (d.Catégorie === 'Objets/IoT'){
                                return 300
                            } else if (d.Catégorie === 'Réseaux mobile et THD'){
                                return 250
                            }  else if (d.Catégorie === 'Amateur'){
                                return 180
                            } else if (d.Catégorie === 'PMSE'){
                                return 120
                            } else if (d.Catégorie === 'Satellite'){
                                return 150
                            } else if (d.Catégorie === 'Faisceaux hertziens'){
                                return 100
                            } else if (d.Catégorie === 'Autres'){
                                return 580
                            }
                        })
                        .attr("name", (d, i) => d.Application)
                        .attr("fill", (d, i) => {
                            if (d.Catégorie === 'Objets/IoT'){
                                return 'rgb(55, 86, 157)'
                            } else if (d.Catégorie === 'Réseaux mobile et THD'){
                                return 'rgb(127, 190, 174)'
                            }  else if (d.Catégorie === 'Amateur'){
                                return 'rgb(194, 44, 119)'
                            } else if (d.Catégorie === 'PMSE'){
                                return 'rgb(210, 47, 47)'
                            } else if (d.Catégorie === 'Satellite'){
                                return 'rgb(75, 172, 208)'
                            } else if (d.Catégorie === 'Faisceaux hertziens'){
                                return 'rgb(242, 157, 75)'
                            } else if (d.Catégorie === 'Autres'){
                                return 'rgb(240, 237, 231)'
                            }
                        })
                        .attr("style", "cursor: pointer;")
                        svg.append("g")
                            .attr("transform", "translate(2, " + 580 +")")
                            .call(xAxis)

    }

    componentDidMount() {
        this.drawChart();
    }

    render (){
        return <div id={"#" + this.props.id}  style={{cursor: 'pointer'}} onClick={this.test}></div>
    }
}

export default BarChart;
