                margin: '0px 2px',
                height: '550px',
                width: '28%',
            },
            secondContainer: {
                backgroundColor: 'rgb(127, 190, 174)',
                margin: '0px 2px',
                height: '550px',
                width: '19%',
            },
            thirdContainer: {
                backgroundColor: 'rgb(55, 86, 157)',
                margin: '0px 2px',
                height: '550px',
                width: '28%',
            },
            fourthContainer: {
                backgroundColor: 'rgb(55, 86, 157)',
                margin: '0px 2px',
                height: '550px',
                width: '25%',
            }
        }
        style.firstContainer.backgroundColor= 'rgb(55, 86, 157)';
        style.thirdContainer.backgroundColor = 'rgb(55, 86, 157)';
        style.fourthContainer.backgroundColor = 'rgb(127, 190, 174)';
