import React, { Component } from 'react';
import classes from './Container.css';
import Radium from 'radium';
import Pastille from './Pastille/Pastille';
import Modal from 'react-modal';
import LeftModal from './LeftModal/LeftModal';
import RightModal from './RightModal/RightModal';
import SecondLeftModal from './SecondLeftModal/SecondLeftModal';
import Themes from './Themes/Themes';
import BarChart from './BarChart/BarChart';
import Categorie from './Categorie/Categorie';
import BottomBar from './BottomBar/BottomBar';
import UnderCategories from './underCategories/underCategories';


const leftModalStyle = { 
  overlay: {
    width: '340px',
    flexGrow: '1',
    zIndex: '1',
    boxShadow: '1px 0px 1px rgba(0, 0, 0, 0.15)',
    marginTop: '80px',
  }, 
  content: {
    backgroundColor: 'wihte',
    width: '100%',
    height: '100%',
    top: '0',
    bottom: '0',
    left: '0',
    right: '0',
    padding: '0',
    justifyContent: 'flex-end',
    border: '0px',
    borderRadius: '0px'
  }
}

const leftSecondModalStyle = { 
  overlay: {
    width: '100%',
    flexGrow: '1',
    zIndex: '1',
    boxShadow: '1px 0px 1px rgba(0, 0, 0, 0.15)',
    marginTop: '80px',
    backgroundColor: 'rgba(0, 0, 0, 0.15)',
  }, 
  content: {
    backgroundColor: 'rgba(0, 0, 0, 0.45)',
    width: '100%',
    height: '100%',
    top: '0',
    bottom: '0',
    left: '0',
    right: '0',
    padding: '0',
    justifyContent: 'flex-end',
    border: '0px',
    borderRadius: '0px'
  }
}

const rightModalStyle = { 
  overlay: {
    width: '340px',
    flexGrow: '1',
    zIndex: '1',
    boxShadow: '1px 0px 1px rgba(0, 0, 0, 0.15)',
    marginTop: '80px',
    alignSelf: 'flex-end',
    left: 'auto'
  }, 
  content: {
    backgroundColor: 'wihte',
    width: '100%',
    height: '100%',
    top: '0',
    bottom: '0',
    left: 'auto',
    right: '0',
    padding: '0',
    justifyContent: 'flex-end',
    border: '0px',
    borderRadius: '0px'
  }
}



class container extends Component {
    state = {
        theme: '',
        categorie: '',
        underCategorie: '',
        whichUnder: 0,
        un: false,
        deux: false,
        trois: false,
        quatre: false,
    }

    openHoverUnHandler = () => {
        this.setState({un: true})
        console.log('un:');
    }
    openHoverDeuxHandler = () => {
        this.setState({deux: true})
        console.log('deu');
    }
    openHoverTroisHandler = () => {
        this.setState({trois: true})
        console.log('tro');
    }
    openHoverQuatreHandler = () => {
      this.setState({quatre: true})
      console.log('qua');
    }

    closeHoverUnHandler = () => {
      this.setState({un: true})
    }
    closeHoverDeuxHandler = () => {
        this.setState({deux: true})
    }
    closeHoverTroisHandler = () => {
        this.setState({trois: true})
    }
    closeHoverQuatreHandler = () => {
      this.setState({quatre: true})
    }

    changeThemeHandler = (theme) => {
      this.props.openTheme();
      this.setState({theme: theme})
      this.props.closeThird();
    }

    changeCategorieHandler = (categorie) => {
      this.setState({categorie: categorie})
      this.props.closeSpectre();
    }

    changeUnderCategorieHandler = (underCategorie, wichUnder) => {
      this.setState({underCategorie: underCategorie})
      this.setState({wichUnder: wichUnder})
    }

    render (){
       this.changeThemeHandler = this.changeThemeHandler.bind(this);
       this.changeCategorieHandler = this.changeCategorieHandler.bind(this);
       this.changeUnderCategorieHandler = this.changeUnderCategorieHandler.bind(this);

        return (        
        <div className={classes.container}>
          { this.props.theme ? 
          <Themes 
            theme={this.state.theme}/> : null}
            { this.props.spectre ? 
              <div className={classes.testSpectre}>
                <span style={{width: '1680px', height: '10px', position: 'absolute', left: 0}} id="spectre1"></span>
                <span style={{width: '1680px', height: '10px', position: 'absolute', left: 1680}} id="spectre2"></span>
                <span style={{width: '1680px', height: '10px', position: 'absolute', left: 3360}} id="spectre3"></span>
                <span style={{width: '1680px', height: '10px', position: 'absolute', left: 5040}} id="spectre4"></span>
                <span style={{width: '1680px', height: '10px', position: 'absolute', left: 6720}} id="spectre5"></span>
                <span style={{width: '1680px', height: '10px', position: 'absolute', left: 8400}} id="spectre6"></span>
                <span style={{width: '1680px', height: '10px', position: 'absolute', left: 10080}} id="spectre7"></span>
                <span style={{width: '1680px', height: '10px', position: 'absolute', left: 11760}} id="spectre8"></span>
                <span style={{width: '1680px', height: '10px', position: 'absolute', left: 13440}} id="spectre9"></span>
                <span style={{width: '1680px', height: '10px', position: 'absolute', left: 15120}} id="spectre10"></span>
                <div className={classes.testSpectreSD} id="spectre">
                  <BarChart 
                    width={5600} 
                    height={580}
                    categorie={this.changeCategorieHandler}
                    openCategorie={this.props.openCategorie}
                    nameSpectre={this.props.nameSpectre}/>
                </div>
              </div> : null}
            { this.props.categorie ? 
                <Categorie 
                  categorie={this.state.categorie} 
                  closeCategorie={this.props.closeCategorie} 
                  openSpectre={this.props.openSpectre}
                  nameSpectre={this.props.nameSpectre}
                  openUnderCategorie={this.props.openUnderCategorie}
                  closeUnderCategorie={this.props.closeUnderCategorie}
                  underCategorie={this.changeUnderCategorieHandler}/> : null}
             { this.props.underCategorie ? 
                <UnderCategories
                  categorie={this.changeCategorieHandler}
                  theme={this.props.theme}
                  openCategorie={this.props.openCategorie}
                  closeTheme={this.props.closeTheme}
                  categorieIsOpen={this.props.categorie} 
                  underCategorie={this.state.underCategorie}
                  closeUnderCategorie={this.props.closeUnderCategorie}
                  nameSpectre={this.props.nameSpectre}
                  openSpectre={this.props.openSpectre}
                  wichUnder={this.state.wichUnder}
                  setSpectreTopBarToOne={this.props.setSpectreTopBarToOne}/> : null}
          <BottomBar 
            categorie={this.changeCategorieHandler}
            theme={this.props.theme}
            openCategorie={this.props.openCategorie}
            closeTheme={this.props.closeTheme}
            categorieIsOpen={this.props.categorie}
            underCategorieIsOpen={this.props.underCategorie}
            nameSpectre={this.props.nameSpectre}
            setSpectreTopBarToOne={this.props.setSpectreTopBarToOne}
            closeUnderCategorie={this.props.closeUnderCategorie}/>
          <Modal 
            isOpen={this.props.modalThird}
            onRequestClose={this.props.closeThird}
            ariaHideApp={false}
            style={leftModalStyle}
            >
              <LeftModal 
                leftClose={this.props.closeThird}
                openSecond={this.props.openSecond}
                closeSecond={this.props.closeSecond}
                theme={this.changeThemeHandler}
                closeSpectre={this.props.closeSpectre}
                closeCategorie={this.props.closeCategorie}
                nameSpectre={this.props.nameSpectre}
                setSpectreTopBarToOne={this.props.setSpectreTopBarToOne}
                closeUnderCategorie={this.props.closeUnderCategorie}/>
            </Modal>
            <Pastille
              leftOpen={this.props.openThird}
              rightOpen={this.props.open}
              />
            <Modal 
              isOpen={this.props.modal}
              onRequestClose={this.props.close}
              ariaHideApp={false}
              style={rightModalStyle}
              >
              <RightModal 
                rightClose={this.props.close}/>
            </Modal>
            <Modal 
              isOpen={this.props.modalSecond}
              onRequestClose={this.props.closeSecond}
              ariaHideApp={false}
              style={leftSecondModalStyle}
              >
                <SecondLeftModal
                  close={this.props.closeSecond}
                  theme={this.changeThemeHandler}
                  closeSpectre={this.props.closeSpectre}
                  closeCategorie={this.props.closeCategorie}
                  closeUnderCategorie={this.props.closeUnderCategorie}
                  un={this.state.un}
                  deux={this.state.deux}
                  trois={this.state.trois}
                  quatre={this.state.quatre}
                  openHoverUnHandler={this.openHoverUnHandler}
                  openHoverDeuxHandler={this.openHoverDeuxHandler}
                  openHoverTroisHandler={this.openHoverUnTroisndler}
                  openHoverQuatreHandler={this.openHoverUnHanQuatreer}
                  closeHoverUnHandler={this.closeHoverUnHandler}
                  closeHoverDeuxHandler={this.closeHoverDeuxHandler}
                  closeHoverTroisHandler={this.closeHoverUnTroisndler}
                  closeHoverQuatreHandler={this.closeHoverUnHanQuatreer}/>
            </Modal>
        </div>
        );
    }
}

export default Radium(container);
