import React, { Component } from 'react';
import classes from './Header.css';
import LogoSpectre from './Logo/logo';
import Radium from 'radium';
import Modal from 'react-modal';
import SpectreTopBar from './SpectreTopBar/SpectreTopBar';
import RightMenu from './RightMenu/RightMenu';
import ModalTopBar from './ModalTopBar/ModalTopBar';

const modalStyle = { 
    overlay: {
      width: '100vw',
      height: '100vh',
      zIndex: '10',
    }, 
    content: {
      backgroundColor: 'rgb(221, 96, 92)',
      width: '100%',
      height: '100%',
      top: '0',
      bottom: '0',
      left: '0',
      right: '0',
      padding: '0',
      justifyContent: 'flex-end',
      border: '0px',
      borderRadius: '0px'
    }
}

class header extends Component {
  state = {
    modalIsOpen: false,
  }

  openModal () {
    this.setState({modalIsOpen: true})
  }

  closeModal () {
    this.setState({modalIsOpen: false});
    document.getElementById('pastille').style.visibility = 'visible';
  }



  render() {
    this.openModal = this.openModal.bind(this);
    this.closeModal = this.closeModal.bind(this);
    if (this.state.modalIsOpen)
      document.getElementById('pastille').style.visibility = 'hidden';

    return (        
      <div className={classes.Header}>
        <LogoSpectre
          closeTheme={this.props.closeTheme}/>
        <SpectreTopBar/>
        <RightMenu menu={this.openModal} open={this.props.open} close={this.props.close} modal={this.props.modal}/>
        <Modal 
          isOpen={this.state.modalIsOpen}
          onRequestClose={() => {this.setState({modalIsOpen: false})}}
          ariaHideApp={false}
          style={modalStyle}>
          <ModalTopBar 
            close={this.closeModal}
            closeTheme={this.props.closeTheme}
            openSecond={this.props.openSecond}/>
        </Modal>
      </div>
    );
  }
}

export default Radium(header);
