import React, {Component} from 'react';
import * as d3 from "d3";
import datas from '../../../assets/baseddarcep.json';

class BarChart extends Component {
    test() {
        console.log('ok');
    }


    drawChart() {
        const xscale = d3.scaleLinear();
                xscale.domain([1000000000, 275000000000]);
                xscale.range([0, this.props.width]);

        const xAxis = d3.axisBottom()
                        .scale(xscale)

        const svg = d3.select("#spectre")
                        .append("svg")
                        .attr("width", this.props.width + 10)
                        .attr("height", this.props.height + 20)
                    svg.selectAll("rect")
                        .data(datas)
                        .enter()
                        .append("rect")
                        .attr("id", (d, i) => {
                            if (d.Catégorie === 'Objets/IoT'){
                               return 'test'
