import React, { Component } from 'react';
import classes from './SecondLeftModal.css';
import Radium from 'radium';
import { Icon } from 'react-icons-kit';
import {x} from 'react-icons-kit/feather/x';
import {home} from 'react-icons-kit/feather/home';
import {androidBus} from 'react-icons-kit/ionicons/androidBus';
import {calendar} from 'react-icons-kit/feather/calendar';
import {radio} from 'react-icons-kit/feather/radio';


//noir
import { ReactComponent as Transport} from '../../../assets/noir/arcep_picto_noir_transports.svg';
import { ReactComponent as Maison} from '../../../assets/noir/arcep_picto_noir_maison.svg';
import { ReactComponent as Concert} from '../../../assets/noir/arcep_picto_noir_events_concerts.svg';
import { ReactComponent as SmartCity} from '../../../assets/noir/arcep_picto_noir_smart_cities.svg';

//blanc
import { ReactComponent as ConcertBlanc} from '../../../assets/blanc/arcep_picto_events_concerts.svg';
import { ReactComponent as SmartCityBlanc} from '../../../assets/blanc/arcep_picto_smart_cities.svg';
import { ReactComponent as MaisonBlanche} from '../../../assets/blanc/arcep_picto_maison.svg';
import { ReactComponent as TransportBlanc} from '../../../assets/blanc/arcep_picto_transports.svg';

class secondLeftModal extends Component {
  render () {
    return (        
      <div className={classes.SecondLeftModal}>
        <div className={classes.Button}>
          <Icon size={30} icon={x} className={classes.Cross} onClick={this.props.close}/>
        </div>
        <div className={classes.Title}>
          <p>Les ondes qui vous entourent ?<br/> Venez <span> découvrir nos thématiques</span>.</p>
        </div>
        <div className={classes.Theme}>
          <div className={classes.rowSquare}>
            <div className={classes.square} onMouseOver={this.props.openHoverUnHandler} onMouseOut={this.props.closeHoverUnHandler} onClick={() => {this.props.closeUnderCategorie(); this.props.theme(1); this.props.closeSpectre();  this.props.closeCategorie(); this.props.close();}}>
              {this.props.un ? <MaisonBlanche  className={classes.logoUnderCat}/> : <Maison  className={classes.logoUnderCat}/>}
              <p >À la maison</p>
            </div>
            <div className={classes.square} onMouseOver={this.props.openHoverDeuxHandler} onMouseOut={this.props.closeHoverDeuxHandler} onClick={() => {this.props.closeUnderCategorie(); this.props.theme(2); this.props.closeSpectre();  this.props.closeCategorie(); this.props.close();}}>
              {this.props.deux ? <TransportBlanc  className={classes.logoUnderCat}/> : <Transport  className={classes.logoUnderCat}/>}
              <p>Transports en commun</p>
            </div>
          </div>
          <div className={classes.rowSquare}>
            <div className={classes.square} onMouseOver={this.props.openHoverTroisHandler} onMouseOut={this.props.closeHoverTroisHandler} onClick={() => {this.props.closeUnderCategorie(); this.props.theme(3); this.props.closeSpectre();  this.props.closeCategorie(); this.props.close();}}>
              {this.props.trois ? <ConcertBlanc  className={classes.logoUnderCat}/> : <Concert  className={classes.logoUnderCat}/>}
              <p>Concerts et évènements sportifs</p>
            </div>
            <div className={classes.square} onMouseOver={this.props.openHoverQuatreHandler} onMouseOut={this.props.closeHoverQuatreHandler} onClick={() => {this.props.closeUnderCategorie(); this.props.theme(4); this.props.closeSpectre();  this.props.closeCategorie(); this.props.close();}}>
              {this.props.quatre ? <SmartCityBlanc  className={classes.logoUnderCat}/> : <SmartCity  className={classes.logoUnderCat}/>}
              <p>Internet des objets / Smart cities</p>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default Radium(secondLeftModal);
