import React from 'react';
import classes from './Themes.css';
import Radium from 'radium';
import { Icon } from 'react-icons-kit';
import {iosGameControllerBOutline} from 'react-icons-kit/ionicons/iosGameControllerBOutline';
import {creditCard} from 'react-icons-kit/feather/creditCard';
import {phoneCall} from 'react-icons-kit/feather/phoneCall'
import {wifi} from 'react-icons-kit/iconic/wifi';


let style = {
    firstContainer: {

    },
    secondContainer: {

    },
    thirdContainer: {

    },
    fourthContainer: {

    }
}

let title = {
    black: '',
    grey: ''
}

let firstContainer = null;
let secondContainer = null;

const themes = (props) => {
    if (props.theme === 1){
        style = {
            firstContainer: {
                backgroundColor: 'rgb(127, 190, 174)',
                margin: '0px 2px',
                height: '550px',
                width: '28%',
            },
            secondContainer: {
                backgroundColor: 'rgb(127, 190, 174)',
                margin: '0px 2px',
                height: '550px',
                width: '19%',
            },
            thirdContainer: {
                backgroundColor: 'rgb(127, 190, 174)',
                margin: '0px 2px',
                height: '550px',
                width: '28%',
            },
            fourthContainer: {
                backgroundColor: 'rgb(55, 86, 157)',
                margin: '0px 2px',
                height: '550px',
                width: '25%',
            }
        }
        title = {
            black: 'À la maison,',
            grey: ' qu’est-ce qui m’entoure dans mon appartement ?'
        }
        firstContainer = (
            <div style={style.firstContainer}>
                <div className={classes.alignContainer}>
                    <Icon icon={iosGameControllerBOutline} size={50}/>
                    <p className={classes.title}>Wifi appartement</p>
                    <p className={classes.text}>Les assistants vocaux, les ordinateurs portables, les tablettes, les smartphones, les consoles de jeu... peuvent être connectés à Internet via le signal Wifi de la box internet.  </p>
                </div>
            </div>
        )
        secondContainer = (
            <div style={style.secondContainer}>
                <div className={classes.alignContainer}>
                    <Icon icon={iosGameControllerBOutline} size={50}/>
                    <p className={classes.title}>Wifi appartement</p>
                    <p className={classes.text}>Les assistants vocaux, les ordinateurs portables, les tablettes, les smartphones, les consoles de jeu... peuvent être connectés à Internet via le signal Wifi de la box internet.  </p>
                </div>
            </div>
        )
    } else if (props.theme === 2){
        style = {
            firstContainer: {
                backgroundColor: 'rgb(55, 86, 157)',
                margin: '0px 2px',
                height: '550px',
                width: '28%',
            },
            secondContainer: {
                backgroundColor: 'rgb(127, 190, 174)',
                margin: '0px 2px',
                height: '550px',
                width: '19%',
            },
            thirdContainer: {
                backgroundColor: 'rgb(55, 86, 157)',
                margin: '0px 2px',
                height: '550px',
                width: '28%',
            },
            fourthContainer: {
                backgroundColor: 'rgb(127, 190, 174)',
                margin: '0px 2px',
                height: '550px',
                width: '25%',
            }
        }
        title = {
            black: 'Transports en commun,',
            grey: ' quelles technologies m\'accompagnent dans mes déplacements ?'
        }
        firstContainer = (
            <div style={style.firstContainer}>
                <div className={classes.alignContainer}>
                    <Icon icon={creditCard} size={50}/>
                    <p className={classes.title}>Carte de transport</p>
                    <p className={classes.text}>Les cartes de transport, comme le pass Navigo, utilisent la "Communication en champ proche" ou NFC, qui est l'une des technologie RFID, pour échanger des informations concernant votre forfait avec les portiques ou bornes de sécurité.  </p>
                </div>
            </div>
        )
    } else if (props.theme === 3){
        style = {
            firstContainer: {
                backgroundColor: 'rgb(127, 190, 174)',
                margin: '0px 2px',
                height: '550px',
                width: '25%',
            },
            secondContainer: {
                backgroundColor: 'rgb(127, 190, 174)',
                margin: '0px 2px',
                height: '550px',
                width: '25%',
            },
            thirdContainer: {
                backgroundColor: 'rgb(127, 190, 174)',
                margin: '0px 2px',
                height: '550px',
                width: '25%',
            },
            fourthContainer: {
                backgroundColor: 'rgb(127, 190, 174)',
                margin: '0px 2px',
                height: '550px',
                width: '25%',
            }
        }
        title = {
            black: 'Concerts et évènements sportifs,',
            grey: ' quels dispositifs sont utilisés pour enregistrer et transmettre des events ?'
        }
        firstContainer = (
            <div style={style.firstContainer}>
                <div className={classes.alignContainer}>
                    <Icon icon={phoneCall} size={50}/>
                    <p className={classes.title}>Réseaux mobiles professionnels</p>
                    <p className={classes.text}>Les réseaux mobiles professionnels (souvent rassemblés sous le sigle PMR pour " professionnal mobile radio ") sont des réseaux mobiles d'ampleur généralement locale ou régionale, exploités pour des usages professionnels.</p>
                </div>
            </div>
        )
    } else if (props.theme === 4){
        style = {
            firstContainer: {
                backgroundColor: 'rgb(55, 86, 157)',
                margin: '0px 2px',
                height: '550px',
                width: '33.333%',
            },
            secondContainer: {
                backgroundColor: 'rgb(55, 86, 157)',
                margin: '0px 2px',
                height: '550px',
                width: '33.333%',
            },
            thirdContainer: {
                backgroundColor: 'rgb(55, 86, 157)',
                margin: '0px 2px',
                height: '550px',
                width: '33.333%',
            },
            fourthContainer: {
                display: 'none',
            }
        }
        title = {
            black: 'Internet des objets / Smart cities,',
            grey: ' qu\'est ce qu\'on retrouve dans une smart city ?'
        }
        firstContainer = (
            <div style={style.firstContainer}>
                <div className={classes.alignContainer}>
                    <Icon icon={wifi} size={50}/>
                    <p className={classes.title}>Capteur</p>
                    <p className={classes.text}>Certains besoins nouveaux de l’internet des objets, ont entrainé l’émergence de nouvelles technologies de connectivité mobile. </p>
                </div>
            </div>
        )
    }
    return (        
      <div className={classes.themes}>
          <div className={classes.modalMiddle}>
            <div className={classes.sectionTitle}>
            <p style={{color: 'rgb(147, 147, 147)', fontWeight: 'bold'}}><span style={{color: 'black'}}>{title.black}</span>{title.grey}</p>
            </div>
            <div className={classes.fullWidthContainer}>
                {firstContainer}
                <div style={style.secondContainer}>

                </div>
                <div style={style.thirdContainer}>

                </div>
                <div style={style.fourthContainer}>

                </div>
            </div>
        </div>
      </div>
    );
}

export default Radium(themes);
