import React, { Component } from 'react';
import classes from './Container.css';
import Radium from 'radium';
import Pastille from './Pastille/Pastille';
import Modal from 'react-modal';
import LeftModal from './LeftModal/LeftModal';
import RightModal from './RightModal/RightModal';
import SecondLeftModal from './SecondLeftModal/SecondLeftModal';
import Themes from './Themes/Themes';
import datas from '../../assets/baseddarcep.json';

var myObject = [
  {
    Catégorie: "Tous",
    Application: "Tous",
    Exemples: "Tous",
    Fréquencebasse: 0,
    Fréquencehaute: "275 000 000 000"
  },
  {
    Catégorie: "Objets/IoT",
    Application: "Applications à bande ultralarge (UWB)",
    Exemples: "Radar",
    Fréquencebasse: 0,
    Fréquencehaute: "1 600 000 000"
  },
  {
    Catégorie: "Réseaux mobile et THD",
    Application: "Applications inductives",
    Exemples: "Plaques à induction",
    Fréquencebasse: "9 000",
    Fréquencehaute: "59 750"
  },
  {
    Catégorie: "Objets/IoT",
    Application: "Implants médicaux actifs",
    Exemples: "",
    Fréquencebasse: "9 000",
    Fréquencehaute: "315 000"
  },
  {
    Catégorie: "Autres",
    Application: "Autres applications",
    Exemples: "",
    Fréquencebasse: "9 000",
    Fréquencehaute: "275 000 000 000"
  },
]

const leftModalStyle = { 
  overlay: {
    width: '340px',
    flexGrow: '1',
    zIndex: '1',
    boxShadow: '1px 0px 1px rgba(0, 0, 0, 0.15)',
    marginTop: '80px',
  }, 
  content: {
    backgroundColor: 'wihte',
    width: '100%',
    height: '100%',
    top: '0',
    bottom: '0',
    left: '0',
    right: '0',
    padding: '0',
    justifyContent: 'flex-end',
    border: '0px',
    borderRadius: '0px'
  }
}

const leftSecondModalStyle = { 
  overlay: {
    width: '100%',
    flexGrow: '1',
    zIndex: '1',
    boxShadow: '1px 0px 1px rgba(0, 0, 0, 0.15)',
    marginTop: '80px',
    backgroundColor: 'rgba(0, 0, 0, 0.15)',
  }, 
  content: {
    backgroundColor: 'rgba(0, 0, 0, 0.45)',
    width: '100%',
    height: '100%',
    top: '0',
    bottom: '0',
    left: '0',
    right: '0',
    padding: '0',
    justifyContent: 'flex-end',
    border: '0px',
    borderRadius: '0px'
  }
}

const rightModalStyle = { 
  overlay: {
    width: '340px',
    flexGrow: '1',
    zIndex: '1',
    boxShadow: '1px 0px 1px rgba(0, 0, 0, 0.15)',
    marginTop: '80px',
    alignSelf: 'flex-end',
    left: 'auto'
  }, 
  content: {
    backgroundColor: 'wihte',
    width: '100%',
    height: '100%',
    top: '0',
    bottom: '0',
    left: 'auto',
    right: '0',
    padding: '0',
    justifyContent: 'flex-end',
    border: '0px',
    borderRadius: '0px'
  }
}

let styletest = {
    backgroundColor: '',
    width: '',
    height: '',
}

class container extends Component {
    state = {
        theme: '',
    }

    changeThemeHandler = (theme) => {
      this.props.openTheme();
      this.setState({theme: theme})
      this.props.closeThird();
    }

    render (){
       this.changeThemeHandler = this.changeThemeHandler.bind(this);
        return (        
        <div className={classes.container}>
          { this.props.theme ? 
          <Themes 
            theme={this.state.theme}/> : null}
            <div className={classes.testSpectre}>
              <div className={classes.testSpectreSD}>
                {
                  datas.map((data, i) => {
                    let width = (data.Fréquencehaute / 1000000 - data.Fréquencebasse / 1000000);
                    if (width < 1)
                      width = 1;
                    data.Fréquencebasse = data.Fréquencebasse / 1000000;
                    if (data.Catégorie === 'Objets/IoT'){
                      styletest = {
                        backgroundColor: 'rgb(55, 86, 157)',
                        width: width + 'px',
                        height: '250px',
                        position: 'absolute',
                        zIndex: 1,
                        left: data.Fréquencebasse,
                      }
                    } else if (data.Catégorie === 'Réseaux mobile et THD'){
                      styletest = {
                        backgroundColor: 'rgb(127, 190, 174)',
                        width: width,
                        height: '200px',
                        position: 'absolute',
                        zIndex: 2,
                        top: '150px',
                        left: data.Fréquencebasse,
                      }
                    }  else if (data.Catégorie === 'Amateur'){
                      styletest = {
                        backgroundColor: 'rgb(194, 44, 119)',
                        width: width,
                        height: '300px',
                        position: 'absolute',
                        zIndex: 2,
                        top: '100px',
                        left: data.Fréquencebasse,
                      }
                    } else if (data.Catégorie === 'PMSE'){
                      styletest = {
                        backgroundColor: 'rgb(210, 47, 47)',
                        width: width,
                        height: '100px',
                        position: 'absolute',
                        zIndex: 2,
                        top: '400px',
                        left: data.Fréquencebasse,
                      }
                    } else if (data.Catégorie === 'Satellite'){
                      styletest = {
                        backgroundColor: 'orange',
                        width: width,
                        height: '100px',
                        position: 'absolute',
                        zIndex: 2,
                        left: data.Fréquencebasse,
                      }
                    } else if (data.Catégorie === 'Faisceaux hertziens'){
                      styletest = {
                        backgroundColor: 'rgb(242, 157, 75)',
                        width: width,
                        height: '100px',
                        position: 'absolute',
                        zIndex: 2,
                        left: data.Fréquencebasse,
                      }
                    } else if (data.Catégorie === 'Autres'){
                      styletest = {
                        backgroundColor: 'rgb(240, 237, 231)',
                        width: width,
                        height: '250px',
                        position: 'absolute',
                        zIndex: 1,
                        top: '250px',
                        left: data.Fréquencebasse,
                      }
                    }
                    else if (data.Catégorie === 'Tous'){
                      styletest = {
                        backgroundColor: 'white',
                        width: width,
                        height: '500px',
                        position: 'absolute',
                        zIndex: 0,
                        left: data.Fréquencebasse,
                      }
                    }
                    return <div style={styletest} key={i}  name={data.Catégorie}></div>
                  })
                }
              </div>
            </div>
          <Modal 
            isOpen={this.props.modalThird}
            onRequestClose={this.props.closeThird}
            ariaHideApp={false}
            style={leftModalStyle}
            >
              <LeftModal 
                leftClose={this.props.closeThird}
                openSecond={this.props.openSecond}
                closeSecond={this.props.closeSecond}
                theme={this.changeThemeHandler}/>
            </Modal>
            <Pastille
              leftOpen={this.props.openThird}
              rightOpen={this.props.open}
              />
            <Modal 
              isOpen={this.props.modal}
              onRequestClose={this.props.close}
              ariaHideApp={false}
              style={rightModalStyle}
              >
              <RightModal 
                rightClose={this.props.close}/>
            </Modal>
            <Modal 
              isOpen={this.props.modalSecond}
              onRequestClose={this.props.closeSecond}
              ariaHideApp={false}
              style={leftSecondModalStyle}
              >
                <SecondLeftModal
                  close={this.props.closeSecond}
                  theme={this.changeThemeHandler}/>
            </Modal>
        </div>
        );
    }
}

export default Radium(container);
