import React from 'react';
import classes from './Categorie.css';
import Radium from 'radium';

let firstContainer = null;
let secondContainer = null;
let thirdContainer = null;
let fourthContainer = null;

const categorie = (props) => {
    if (props.theme === 1){
        style = {
            firstContainer: {
                backgroundColor: 'rgb(127, 190, 174)',
                margin: '0px 2px',
                height: '550px',
                width: '28%',
            },
            secondContainer: {
                backgroundColor: 'rgb(127, 190, 174)',
                margin: '0px 2px',
                height: '550px',
                width: '19%',
            },
            thirdContainer: {
                backgroundColor: 'rgb(127, 190, 174)',
                margin: '0px 2px',
                height: '550px',
                width: '28%',
            },
            fourthContainer: {
                backgroundColor: 'rgb(55, 86, 157)',
                margin: '0px 2px',
                height: '550px',
                width: '25%',
            }
        }
        title = {
            black: 'À la maison,',
            grey: ' qu’est-ce qui m’entoure dans mon appartement ?'
        }
        firstContainer = (
            <div style={style.firstContainer}>
                <div className={classes.alignContainer}>
                </div>
            </div>
        )
        secondContainer = (
            <div style={style.secondContainer}>
                <div className={classes.alignContainer}>
                </div>
            </div>
        )
        thirdContainer = (
            <div style={style.thirdContainer}>
                <div className={classes.alignContainer}>
                </div>
            </div>
        )
        fourthContainer = (
            <div style={style.fourthContainer}>
                <div className={classes.alignContainer}>
                </div>
            </div>
        )
    } else if (props.theme === 2){
        style = {
            firstContainer: {
                backgroundColor: 'rgb(55, 86, 157)',
                margin: '0px 2px',
                height: '550px',
                width: '28%',
            },
            secondContainer: {
                backgroundColor: 'rgb(127, 190, 174)',
                margin: '0px 2px',
                height: '550px',
                width: '19%',
            },
            thirdContainer: {
                backgroundColor: 'rgb(55, 86, 157)',
                margin: '0px 2px',
                height: '550px',
                width: '28%',
            },
            fourthContainer: {
                backgroundColor: 'rgb(127, 190, 174)',
                margin: '0px 2px',
                height: '550px',
                width: '25%',
            }
        }
        title = {
            black: 'Transports en commun,',
            grey: ' quelles technologies m\'accompagnent dans mes déplacements ?'
        }
        firstContainer = (
            <div style={style.firstContainer}>
                <div className={classes.alignContainer}>
                </div>
            </div>
        )
        secondContainer = (
            <div style={style.secondContainer}>
                <div className={classes.alignContainer}>
                </div>
            </div>
        )
        thirdContainer = (
            <div style={style.thirdContainer}>
                <div className={classes.alignContainer}>
                </div>
            </div>
        )
        fourthContainer = (
            <div style={style.fourthContainer}>
                <div className={classes.alignContainer}>
                </div>
            </div>
        )
    } else if (props.theme === 3){
        style = {
            firstContainer: {
                backgroundColor: 'rgb(127, 190, 174)',
                margin: '0px 2px',
                height: '550px',
                width: '25%',
            },
            secondContainer: {
                backgroundColor: 'rgb(127, 190, 174)',
                margin: '0px 2px',
                height: '550px',
                width: '25%',
            },
            thirdContainer: {
                backgroundColor: 'rgb(127, 190, 174)',
                margin: '0px 2px',
                height: '550px',
                width: '25%',
            },
            fourthContainer: {
                backgroundColor: 'rgb(127, 190, 174)',
                margin: '0px 2px',
                height: '550px',
                width: '25%',
            }
        }
        title = {
            black: 'Concerts et évènements sportifs,',
            grey: ' quels dispositifs sont utilisés pour enregistrer et transmettre des events ?'
        }
        firstContainer = (
            <div style={style.firstContainer}>
                <div className={classes.alignContainer}>
                </div>
            </div>
        )
        secondContainer = (
            <div style={style.secondContainer}>
                <div className={classes.alignContainer}>
                </div>
            </div>
        )
        thirdContainer = (
            <div style={style.thirdContainer}>
                <div className={classes.alignContainer}>
                </div>
            </div>
        )
        fourthContainer = (
            <div style={style.fourthContainer}>
                <div className={classes.alignContainer}>
                </div>
            </div>
        )
    } else if (props.theme === 4){
        style = {
            firstContainer: {
                backgroundColor: 'rgb(55, 86, 157)',
                margin: '0px 2px',
                height: '550px',
                width: '33.333%',
            },
            secondContainer: {
                backgroundColor: 'rgb(55, 86, 157)',
                margin: '0px 2px',
                height: '550px',
                width: '33.333%',
            },
            thirdContainer: {
                backgroundColor: 'rgb(55, 86, 157)',
                margin: '0px 2px',
                height: '550px',
                width: '33.333%',
            },
            fourthContainer: {
                display: 'none',
            }
        }
        title = {
            black: 'Internet des objets / Smart cities,',
            grey: ' qu\'est ce qu\'on retrouve dans une smart city ?'
        }
        firstContainer = (
            <div style={style.firstContainer}>
                <div className={classes.alignContainer}>
                </div>
            </div>
        )
        secondContainer = (
            <div style={style.secondContainer}>
                <div className={classes.alignContainer}>
                </div>
            </div>
        )
        thirdContainer = (
            <div style={style.thirdContainer}>
                <div className={classes.alignContainer}>
                </div>
            </div>
        )
        fourthContainer = null;
    }
    return (        
      <div className={classes.themes}>
          <div className={classes.modalMiddle}>
            <div className={classes.sectionTitle}>
            <p style={{color: 'rgb(147, 147, 147)', fontWeight: 'bold'}}><span style={{color: 'black'}}>{title.black}</span>{title.grey}</p>
            </div>
            <div className={classes.fullWidthContainer}>
                {firstContainer}
                {secondContainer}
                {thirdContainer}
                {fourthContainer}
            </div>
        </div>
      </div>
    );
}

export default Radium(categorie);
