import React, { Component } from 'react';
import classes from './Container.css';
import Radium from 'radium';
import Pastille from './Pastille/Pastille';
import Modal from 'react-modal';
import LeftModal from './LeftModal/LeftModal';
import RightModal from './RightModal/RightModal';
import SecondLeftModal from './SecondLeftModal/SecondLeftModal';

const leftModalStyle = { 
  overlay: {
    width: '340px',
    flexGrow: '1',
    zIndex: '1',
    boxShadow: '1px 0px 1px rgba(0, 0, 0, 0.15)',
    marginTop: '80px',
  }, 
  content: {
    backgroundColor: 'wihte',
    width: '100%',
    height: '100%',
    top: '0',
    bottom: '0',
    left: '0',
    right: '0',
    padding: '0',
    justifyContent: 'flex-end',
    border: '0px',
    borderRadius: '0px'
  }
}

const leftSecondModalStyle = { 
  overlay: {
    width: '100%',
    flexGrow: '1',
    zIndex: '1',
    boxShadow: '1px 0px 1px rgba(0, 0, 0, 0.15)',
    marginTop: '80px',
    backgroundColor: 'rgba(0, 0, 0, 0.15)',
  }, 
  content: {
    backgroundColor: 'rgba(0, 0, 0, 0.45)',
    width: '100%',
    height: '100%',
    top: '0',
    bottom: '0',
    left: '0',
    right: '0',
    padding: '0',
    justifyContent: 'flex-end',
    border: '0px',
    borderRadius: '0px'
  }
}

const rightModalStyle = { 
  overlay: {
    width: '340px',
    flexGrow: '1',
    zIndex: '1',
    boxShadow: '1px 0px 1px rgba(0, 0, 0, 0.15)',
    marginTop: '80px',
    alignSelf: 'flex-end',
    left: 'auto'
  }, 
  content: {
    backgroundColor: 'wihte',
    width: '100%',
    height: '100%',
    top: '0',
    bottom: '0',
    left: 'auto',
    right: '0',
    padding: '0',
    justifyContent: 'flex-end',
    border: '0px',
    borderRadius: '0px'
  }
}

class container extends Component {
    state = {
        page: '',
        leftModalIsOpen: false,
        // rightModalIsOpen: false,
        leftSecondModalIsOpen: false,
    }

    // TODO handler for     

    openLeftModal () {
      this.setState({leftModalIsOpen: true});
      document.getElementById('pastilleLeft').style.visibility = 'hidden';
    }

    openSecondLeftModal () {
      this.setState({leftSecondModalIsOpen: true});
      this.setState({leftModalIsOpen: false});
      document.getElementById('pastilleLeft').style.visibility = 'visible';
      document.getElementById('pastilleLeft').style.pointerEvents = 'none';
      document.getElementById('pastilleRight').style.pointerEvents = 'none';
      }

    // openRightModal () {
    //   this.setState({rightModalIsOpen: true});
    //   document.getElementById('pastilleRight').style.visibility = 'hidden';
    // }

    closeLeftModal () {
      this.setState({leftModalIsOpen: false});
      document.getElementById('pastilleLeft').style.visibility = 'visible';
    }

    closeSecondLeftModal () {
      this.setState({leftSecondModalIsOpen: false});
      document.getElementById('pastilleLeft').style.pointerEvents = 'auto';
      document.getElementById('pastilleRight').style.pointerEvents = 'auto';
    }

    // closeRightModal () {
    //   this.setState({rightModalIsOpen: false});
    //   document.getElementById('pastilleRight').style.visibility = 'visible';
    // }

    render (){
        this.openLeftModal = this.openLeftModal.bind(this);
        this.openSecondLeftModal = this.openSecondLeftModal.bind(this);
        // this.openRightModal = this.openRightModal.bind(this);
        this.closeLeftModal = this.closeLeftModal.bind(this);
        this.closeSecondLeftModal = this.closeSecondLeftModal.bind(this);
        // this.closeRightModal = this.closeRightModal.bind(this);
        return (        
        <div className={classes.container}>
           <Modal 
            isOpen={this.state.leftModalIsOpen}
            onRequestClose={() => {this.setState({leftModalIsOpen: false})}}
            ariaHideApp={false}
            style={leftModalStyle}
            >
              <LeftModal 
                leftClose={this.closeLeftModal}
                openSecond={this.openSecondLeftModal}
                closeSecond={this.closeSecondLeftModal}/>
            </Modal>
            <Pastille
              leftOpen={this.openLeftModal}
              rightOpen={this.openRightModal}
              />
            <Modal 
              isOpen={this.props.modal}
              onRequestClose={() => {this.setState({rightModalIsOpen: false})}}
              ariaHideApp={false}
              style={rightModalStyle}
              >
              <RightModal 
                rightClose={this.closeRightModal}/>
            </Modal>
            <Modal 
              isOpen={this.state.leftSecondModalIsOpen}
              onRequestClose={() => {this.setState({leftSecondModalIsOpen: false})}}
              ariaHideApp={false}
              style={leftSecondModalStyle}
              >
                <SecondLeftModal
                  close={this.closeSecondLeftModal}/>
            </Modal>
        </div>
        );
    }
}

export default Radium(container);
